import React, { useState } from 'react';
import Questions from "./Components/Questions"
import ReactPlayer from "react-player";

let questions

let learningOptions =
	[
		{
			questionText: 'Despre ce doresti sa inveti?',
			answerOptions: [
				{ answerText: 'Despre Compania Tesla', choice: 1 },
				{ answerText: 'Istoria Masinilor Electrice', choice: 2 },
				{ answerText: 'Modele de masini electrice', choice: 3 },

			],
		}
];

export default function App() {


	const [currentQuestion, setCurrentQuestion] = useState(0);
	const [showScore, setShowScore] = useState(false);
	const [score, setScore] = useState(0);
	const [learningChoice,setLearningChoice] = useState(0);

	const handleLearningAnswer = (choice) =>{
        switch (choice) {
        	case 1:
        		questions = Questions.brandQuestions()
						setLearningChoice(1)
						console.log("BRAND")
						break
				 case 2:
				    questions = Questions.electricCarsHistoryQuestions()
						setLearningChoice(2)
						break
				case 3:
				    questions = Questions.electricCarsModelsQuestions()
						setLearningChoice(3)
				    break


        }
	}

	const handleAnswerOptionClick = (isCorrect) => {
		if (isCorrect) {
			setScore(score + 1);
		}

		const nextQuestion = currentQuestion + 1;
		if (nextQuestion < questions.length) {
			setCurrentQuestion(nextQuestion);
		} else {
			setShowScore(true);
		}
	};
	if(learningChoice === 0){
		return(
			<div className='app'>
				<div className='question-section'>
					<div className='question-count'>
						<span>Alege ce doresti sa inveti</span>
					</div>

				</div>
				<div className='answer-section'>
					{learningOptions[currentQuestion].answerOptions.map((answerOption) => (
						<button onClick={() => handleLearningAnswer(answerOption.choice)}>{answerOption.answerText}</button>
					))}
				</div>
			</div>
		)
	}
	else{return (
		<div className='app'>
			{showScore ? (
				<div className='score-section'>
					Ai raspuns corect la {score} din {questions.length -1} intrebari
				</div>
			) : (
				<>
					<div className='question-section'>
						<div className='question-count'>
					{currentQuestion!==0 && 	<div>
								<span>Intrebarea {currentQuestion}</span>/{questions.length -1}
							</div>}
							{currentQuestion === 0 && learningChoice ===1 &&
								<div>
							  <ReactPlayer 
							  	width={420} 
								height={348}
								url="https://www.youtube.com/watch?v=pQiT2U5E9tI"
							  />
							</div>
							}
							{currentQuestion === 0 && learningChoice ===2 &&
								<div>
									<ReactPlayer
									width={420} 
									height={348}
										url="https://www.youtube.com/watch?v=FdL2YI9k0PI"
									/>
								</div>
							}

						</div>
						<div className='question-text'>{questions[currentQuestion].questionText}</div>
					</div>
					<div className='answer-section'>
						{questions[currentQuestion].answerOptions.map((answerOption) => (
							<button onClick={() => handleAnswerOptionClick(answerOption.isCorrect)}>{answerOption.answerText}</button>
						))}
					</div>
				</>
			)}
		</div>
	);
}

}
